class SeveritiumInjector{constructor(e){this.Severitium=e}updateSeveritium(e){this.Severitium=e}removeInjectedCSS(){const e=document.querySelectorAll('style[data-resource="SeveritiumCSS"]');for(const t of e)t.remove()}injectCSS(e,t=[]){const o=document.createElement("style");o.textContent=Severitium.CSS[e];for(const e of t)o.setAttribute(e.name,e.value);document.body.appendChild(o)}applyCSS(e){removeInjectedCSS();const t={name:"data-resource",value:"SeveritiumCSS"};for(const{url:o,attributes:r=[]}of e){const e=r.some(e=>"data-resource"===e.name)?r:[t,...r];injectCSS(o,e)}}removeInjectedImages(){const e=document.querySelectorAll('style[data-resource="SeveritiumImage"]');for(const t of e)t.remove()}injectImage(e,t,o=[]){const r=t.replace("SEVERITIUM_PLACEHOLDER",this.Severitium.images[e]),n=document.createElement("style");n.textContent=r;for(const e of o)n.setAttribute(e.name,e.value);document.body.appendChild(n)}applyImages(e){removeInjectedImages();const t={name:"data-resource",value:"SeveritiumImage"};for(const{url:o,style:r,attributes:n=[]}of e){const e=o.replace("SEASON_PLACEHOLDER",_getSeason()),c=n.some(e=>"data-resource"===e.name)?n:[t,...n];injectImage(e,r,c)}}}